import React, {Component} from "react";
import Navbar from "../components/Navbar";
import HazardContainer from "../components/HazardContainer";
import axios from "axios";
axios.defaults.baseURL = process.env.APP_URL;

export default class ReportsPage extends Component {
    constructor(props){
      super(props);
      this.state = {
        reports : [],
      }

    }
    componentDidMount() {
        // need to make the initial call to getData() to populate
        // data right away
        this.getData();

        // Now we need to make it run at a specified interval
        setInterval(this.getData, 5000); // runs every 5 seconds.
    };

    getData(){
      async function loadData(){
        console.log("GETTING REPORT DATA");
        // do something to fetch data from a remote API.
        //
        axios.get(
          "/api/reports"
        ).then((response) =>{
          console.log(response.data);
          this.state.reports = response.data;
        }
      ).catch();

      };
      loadData();
      }




    render() {
        return (
            <div className="content-container">
                <Navbar></Navbar>

                {this.state.reports}


                <h1>This is the reports page.</h1>
                <h1>This is the reports page.</h1>
                <button onClick={this.handleClick}>
                </button>
                <h1>This is the reports page.</h1>
                <h1>This is the reports page.</h1>
                <h1>This is the reports page.</h1>
                <HazardContainer/>
            </div>
        )
    }
}
