import React, {Component} from "react";
import Navbar from "../components/Navbar";
import HazardsMap from "../components/HazardsMap";
import { Modal, Button } from "react-bootstrap";
import "../components/Modal.css"


export default class HazardsPage extends Component {
	constructor(props) {
    	super(props);
	    this.state = {
	      modal: false,
	      name: "",
	      hazardTitle: "",
	      hazardDescription: "",
	    };
  	}
	  handleChange(e) {
	    const target = e.target;
	    const name = target.name;
	    const value = target.value;

	    this.setState({
	      [name]: value
	    });
	  }
	  handleChangeDescription(e) {
	    const target = e.target;
	    const name = target.name;
	    const value = target.value;

	    this.setState({
	      [hazardDescription]: value
	    });
	    console.log(this.state);
	  }

	  handleSubmit(e) {
	    this.setState({ name: this.state.hazardTitle });
	    this.modalClose();
	  }

	  modalOpen() {
	    this.setState({ modal: true });
	  }

	  modalClose() {
	    this.setState({
	      hazardTitle: "",
	      modal: false
	    });
	  }

	  render() {
	    return (
	    <div className="content-container">
	    	  <Navbar />
		      <div className='modal-content' >

		        <h1>Report Center</h1>
		        <a href="javascript:;" onClick={e => this.modalOpen(e)}>
		          Report trouble on the route
		        </a>
		        <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}>
		          <div className="modal">
		          <h2>Report</h2>

		            <label>Enter Title:</label>
		            <input
		              type="text"
		              value={this.state.hazardTitle}
		              name="hazardTitle"
		              onChange={e => this.handleChange(e)}

		            />
		            <label>Description</label>
		            <input
		              type="text"
		              value={this.state.hazardDescription}
		              name="hazardDescription"
		              onChange={e => this.handleChangeDescription(e)}

		            />
		            <button onClick={e => this.handleSubmit(e)} type="button">
		              Click to confirm waypoints
		            </button>
		            <HazardsMap/>


		          </div>
		        </Modal>
		      </div>
		</div>
	    );
	  }
	}
