import React, { Component } from "react";
import { Map, GoogleApiWrapper, StandaloneSearchBox } from "google-maps-react";


export class MapContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      coords : [ {
          name: "Manila",
          title: "Manila",
          lat: 42.356276,
          lng: -71.096876,
          id: 1,
        },
        {
            name: "Taguig",
            title: "Taguig",
            lat: 42.348310,
            lng: -71.081311,
            id: 3,
        },
      ],
      waypoints : [],
    };
  }
    onMapReady = (mapProps, map) => {


      //instantiate directions service and directions renderer
      const directionsService = new this.props.google.maps.DirectionsService();
      const directionsDisplay = new this.props.google.maps.DirectionsRenderer();
      //put directions renderer to render in the map
      directionsDisplay.setMap(map);
      //Getting the first coordinate in the array as the start/origin
      let start = { lat: coords[0].lat, lng: coords[0].lng };
      //Getting the last coordinate in the array as the end/destination
      let end = {
        lat: coords[coords.length - 1].lat,
        lng: coords[coords.length - 1].lng,
      };

      //putting all the coordinates between the first and last coordinate from the array as the waypoints
    //   for (let i = 1; i < coords.length - 1; i++) {
    //     waypoints.push({
    //       location: { lat: coords[i].lat, lng: coords[i].lng },
    //       stopover: true,
    //     });
    //   }

      // directions requests

      let request = {
        origin: start,
        waypoints: waypoints,
        destination: end,
        travelMode: "BICYCLING",
      };

      //show results in the directionsrenderer
      directionsService.route(request, function (result, status) {
        if (status == "OK") {
          directionsDisplay.setDirections(result);
        }
      });

      //setting the autocomplete input
      let card = document.getElementById("pac-card");
      let input = document.getElementById("pac-input");
      let card2 = document.getElementById("pac-card2");
      let input2 = document.getElementById("pac-input2");
      map.controls[this.props.google.maps.ControlPosition.TOP_CENTER].push(card);
      map.controls[this.props.google.maps.ControlPosition.TOP_RIGHT].push(card2);
      let autocomplete = new this.props.google.maps.places.Autocomplete(input);
      let autocomplete2 = new this.props.google.maps.places.Autocomplete(input2);

      // Bind the map's bounds (viewport) property to the autocomplete object,
      // so that the autocomplete requests use the current map bounds for the
      // bounds option in the request.
      autocomplete.bindTo("bounds", map);
      autocomplete2.bindTo("bounds", map);

      // Set the data fields to return when the user selects a place.
      autocomplete.setFields(["address_components", "geometry", "icon", "name"]);
      autocomplete2.setFields(["address_components", "geometry", "icon", "name"]);

      //listener for the places input
      autocomplete.addListener("place_changed", () => {
        console.log(waypoints);
        let place = autocomplete.getPlace();
        if (!place.geometry) {
          // User entered the name of a Place that was not suggested and
          // pressed the Enter key, or the Place Details request failed.
          window.alert("No details available for input: '" + place.name + "'");
          return;
        }

        //Putting the previous last coordinate in the array to be part of the waypoint
        // waypoints.push({
        //   location: {
        //     lat: coords[coords.length - 1].lat,
        //     lng: coords[coords.length - 1].lng,
        //   },
        //   stopover: true,
        // });

        //putting the Place Autocomplete coordinate result in the coords array
        coords[0] = {
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng(),
        };
        // //putting the Place Autocomplete coordinate result the value of the end/destination
        // end = coords[1];

        //changing  request
        request = {
          origin: coords[0],
          waypoints: waypoints,
          destination: coords[1],
          travelMode: "BICYCLING",
        };
        //creating new directions request
        if (coords[0] !== null && coords[1] !== null) {
            directionsService.route(request, function (result, status) {
                if (status == "OK") {
                    directionsDisplay.setDirections(result);
                }
            });
        };


      });





    //listener for the places input
    autocomplete2.addListener("place_changed", () => {
        console.log(waypoints);
        let place = autocomplete2.getPlace();
        if (!place.geometry) {
          // User entered the name of a Place that was not suggested and
          // pressed the Enter key, or the Place Details request failed.
          window.alert("No details available for input: '" + place.name + "'");
          return;
        }

        //Putting the previous last coordinate in the array to be part of the waypoint
        // waypoints.push({
        //   location: {
        //     lat: coords[coords.length - 1].lat,
        //     lng: coords[coords.length - 1].lng,
        //   },
        //   stopover: true,
        // });

        //putting the Place Autocomplete coordinate result in the coords array
        coords[1] = {
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng(),
        };
        // //putting the Place Autocomplete coordinate result the value of the end/destination
        // end = coords[1];

        //changing  request
        request = {
          origin: coords[0],
          waypoints: waypoints,
          destination: coords[1],
          travelMode: "BICYCLING",
        };
        //creating new directions request
        if (coords[0] !== null && coords[1] !== null) {
            directionsService.route(request, function (result, status) {
                if (status == "OK") {
                    directionsDisplay.setDirections(result);
                }
            });
        }
      });
    };

    onMapClick = (t, map, coord) => {
        console.log("andf")
          const { latLng } = coord;
          const lat1 = latLng.lat();
          const lng1 = latLng.lng();
          if(coords[2].lat !== null && coords[3].lat !== null){
            coords[2] = {
              lat: lat1,
              lng: lng1,
            };
            coords[3] = {
              lat: null,
              lng: null,
            };
          }
          else if(coords[2].lat !== null && coords[3].lat === null){
            coords[3] = {
              lat: lat1,
              lng: lng1,
            };


          };
    };


    render() {
      //if (!this.props.loaded) return <div>Loading...</div>;

      return (
        <div>
          <Map
            className="map"
            initialCenter={{ lat: 14.6091, lng: 121.0223 }}
            google={this.props.google}
            onClick={this.onMapClick}
            onReady={this.onMapReady}
            style={{ height: "100%", position: "relative", width: "100%" }}
            zoom={8}
          ></Map>
          <div className="pac-card" id="pac-card">
            <div>
              <div id="title">Add new point</div>

              <div id="pac-container">
                <input
                  id="pac-input"
                  type="text"
                  placeholder="Enter a location"
                />
              </div>
            </div>
          </div>
          <div className="pac-card" id="pac-card2">
            <div>
              <div id="title2">Add new point</div>

              <div id="pac-container2">
                <input
                  id="pac-input2"
                  type="text"
                  placeholder="Enter a location"
                />
              </div>
            </div>
          </div>
          <div style={{ width: 500, height: 500 }} id={this.props.id} />
          <div id="infowindow-content">
            <img src="" width="16" height="16" id="place-icon" />
            <span id="place-name" className="title"></span>
            <br />
            <span id="place-address"></span>
            <br />
            <span id="place-coord"></span>
          </div>
        </div>
      );
    }
  }

export default GoogleApiWrapper({
  apiKey: 'AIzaSyBAVWVu8_U0FfwgBiHVN3q-G5uCQNBAj7Y'
})(MapContainer);
