import React, {Component} from "react";
import Navbar from "../components/Navbar";
import HazardsMap from "../components/HazardsMap";
import { Modal, Button } from "react-bootstrap";


export default class HazardsPage extends Component {
	constructor(props) {
    	super(props);
	    this.state = {
	      modal: false,
	      name: "",
	      modalInputName: ""
	    };
  	}
	  handleChange(e) {
	    const target = e.target;
	    const name = target.name;
	    const value = target.value;

	    this.setState({
	      [name]: value
	    });
	  }

	  handleSubmit(e) {
	    this.setState({ name: this.state.modalInputName });
	    this.modalClose();
	  }

	  modalOpen() {
	    this.setState({ modal: true });
	  }

	  modalClose() {
	    this.setState({
	      modalInputName: "",
	      modal: false
	    });
	  }

	  render() {
	    return (
	      <div className="Hazards" style="float:left;">

	        <h1>Report Center</h1>
	        <a href="javascript:;" onClick={e => this.modalOpen(e)}>
	          Report trouble on the route
	        </a>
	        <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}>
	          <h2>Report</h2>
	          <div className="form-group">
	            <label>Enter Name:</label>
	            <input
	              type="text"
	              value={this.state.modalInputName}
	              name="modalInputName"
	              onChange={e => this.handleChange(e)}
	              className="form-control"
	            />
	            <HazardsMap />
	          </div>
	          <div className="form-group">
	            <button onClick={e => this.handleSubmit(e)} type="button">
	              Save
	            </button>
	          </div>
	        </Modal>
	      </div>
	    );
	  }
	}
